Meteor.startup(function() {
		process.env.MAIL_URL = 'smtp://eagleviewhillwebsite:password123inallcaps@smtp.gmail.com:587/'
	});


Meteor.methods({
  sendEmail: function (to, from, subject, text) {
    check([to, from, subject, text], [String]);

    // Let other method calls from the same client start running,
    // without waiting for the email sending to complete.
    this.unblock();

    //actual email sending method
    Email.send({
      to: 'eagleviewhillwebsite@gmail.com',
      from: 'eagleviewhillwebsite@gmail.com',
      subject: 'New message from property website',
      text: text
    });
  }
});
